import React from "react";
import Bootstrap from 'react-bootstrap/Button';


class Alert extends React.Component {
    constructor(props) {
        super(props)
        this.initialize()

        this.state = {
            show: this.props.show ? true : false
        }
    }

    initialize() {
        var alertList = document.querySelectorAll(`#${this.props.alert}`)
        var alerts = [].slice.call(alertList).map(function (element) {
            return new Bootstrap.Alert(element)
        })
    }

    render() {
        console.log(this.state.show)
        if (!this.state.show) {
            return null
        }

        const showAlert = this.state.show ? "show" : false

        return (
            <React.Fragment>
                <div className={`alert alert-danger alert-dismissible fade ${showAlert} position-fixed top-0 end-0 d-flex align-items-center me-5 mt-5`} role="alert">
                    {/* <svg class="bi flex-shrink-0 me-2" width="24" height="24" role="img" aria-label="Danger:"><use xlink:href="#exclamation-triangle-fill" /></svg> */}
                    <p>
                        Acci√≥n no permitida
                    </p>
                    <button type="button" className="btn-close" data-bs-dismiss="alert" aria-label="Close"></button>
                </div>
            </React.Fragment>
        )
    }
}

export default Alert
