/* Framework */
import React from 'react';

/* Style CSS, SASS, LESS */
import './App.css';

/* Components */
import Board from './components/Board';
import ScoreBoard from './components/ScoreBoard';
import Menu from './components/Menu';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      startPlayer: ["white", "black"][Math.floor(Math.random() * 2)],
      nextPlayer: "null",
      emptySquare: "&nbsp;",
      currentSelected: {
        item: null,
        position: "0-0"
      }
    }
  }

  componentDidMount() {
    this.setState({
      nextPlayer: this.state.startPlayer,
    })
  }

  updateCurrentSelected(item, position) {
    this.setState({
      currentSelected: {
        item: item,
        position: position
      }
    })
  }

  updateCurrentPlayer() {
    this.setState({
      nextPlayer: this.state.nextPlayer === "black" ? "white" : "black"
    })
  }

  handleClick(el) {
    console.log(el.currentTarget.classList)
    if (this.state.currentSelected.position !== "0-0" && this.state.currentSelected.item !== el) {
      if (this.state.nextPlayer === el.currentTarget.dataset.player) {
        if (this.state.currentSelected.position !== el.currentTarget.dataset.position) {
          el.currentTarget.appendChild(this.state.currentSelected.item.target.children[0])
          this.updateCurrentSelected(null, "0-0")
          this.updateCurrentPlayer()
        }
      }
    } else {
      this.updateCurrentSelected(el, el.currentTarget.dataset.position)
    }
  }

  juega(T) {

    var elementos = document.querySelectorAll("table, table span"); 

    if(!moviendo && T.firstElementChild){
    padre = T; 
    hijo = T.innerHTML; 

    for(i=0; elementos[i]; i++) elementos[i].classList.add("mano"); 

    T.querySelector("span").style.opacity = ".4"; 

    moviendo = true; 
    }

    else if(moviendo){
    padre.innerHTML = ""; 
    T.innerHTML = hijo; 

    for(i=0; elementos[i]; i++) elementos[i].classList.remove("mano"); 

    moviendo = false; 
    }


    }

  render() {
    return (
      <React.Fragment>
        <Menu />
        <div className="container-fluid">
          <div className="row">
            <div className="col-12 col-lg-10">
              <Board nextPlayer={this.state.nextPlayer} onClick={(el) => this.handleClick(el)} />
            </div>

            <div className="col-12 col-lg-2 order-1 order-lg-2">
              <ScoreBoard nextPlayer={this.state.nextPlayer} />
            </div>
          </div>
        </div>
      </React.Fragment>
    )
  }
}

export default App;
