import React, { useState } from "react";
import { ToastContainer } from "react-bootstrap";
import Toast from 'react-bootstrap/Toast';

class AlertDismissible extends React.Component {
    state = {
        show: false
    }

    constructor(props) {
        super(props)
        console.log('constructor', this.props.show)
        this.state = {
            show: this.props.show
        }
    }

    componentDidMount() {
        console.log('componentDidMount', this.props.show)
        this.setState({
            show: this.props.show
        })
    }

    handleShowAlert(show) {
        console.log('handleShowAlert', this.props.show)
        this.setState({
            show: show
        })
    }

    render() {
        console.log('render', this.state.show)
        if (this.props.show) {
            return (
                <ToastContainer className="mt-5 me-5" position="top-end">
                    <Toast onClose={() => this.handleShowAlert(false)} show={this.state.show} delay={3000} autohide>
                        <Toast.Header>
                            <img
                                src="holder.js/20x20?text=%20"
                                className="rounded me-2"
                                alt=""
                            />
                            <strong className="me-auto">Bootstrap</strong>
                            <small>11 mins ago</small>
                        </Toast.Header>
                        <Toast.Body>Woohoo, you're reading this text in a Toast!</Toast.Body>
                    </Toast>
                </ToastContainer>
            );
        } else {
            return null
        }
    }

}

function Alert(props) {
    const [show, setShow] = useState(props.show)

    if (show) {
        return (
            <ToastContainer className="mt-5 me-5" position="top-end">
                <Toast onClose={() => setShow(false)} show={show} delay={3000} autohide>
                    <Toast.Header>
                        <img
                            src="holder.js/20x20?text=%20"
                            className="rounded me-2"
                            alt=""
                        />
                        <strong className="me-auto">Bootstrap</strong>
                        <small>11 mins ago</small>
                    </Toast.Header>
                    <Toast.Body>Woohoo, you're reading this text in a Toast!</Toast.Body>
                </Toast>
            </ToastContainer>
        );
    } else {
        return null
    }

}

export default Alert
