import React from "react";

class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            board: [
                ["", "A", "B", "C", "D", "E", "F", "G", "H", ""],
                ["8", "R", "N", "B", "Q", "K", "B", "N", "R", "8"],
                ["7", "P", "P", "P", "P", "P", "P", "P", "P", "7"],
                ["6", "", "", "", "", "", "", "", "", "6"],
                ["5", "", "", "", "", "", "", "", "", "5"],
                ["4", "", "", "", "", "", "", "", "", "4"],
                ["3", "", "", "", "", "", "", "", "", "3"],
                ["2", "P", "P", "P", "P", "P", "P", "P", "P", "2"],
                ["1", "R", "N", "B", "Q", "K", "B", "N", "R", "1"],
                ["", "A", "B", "C", "D", "E", "F", "G", "H", ""],
            ],
        }
    }

    getToken(token, color) {
        const w = {
            P: "fas fa-chess-pawn fa-3x text-white",
            N: "fas fa-chess-knight fa-3x text-white",
            B: "fas fa-chess-bishop fa-3x text-white",
            R: "fas fa-chess-rook fa-3x text-white",
            Q: "fas fa-chess-queen fa-3x text-white",
            K: "fas fa-chess-king fa-3x text-white",
        };

        const b = {
            P: "fas fa-chess-pawn fa-3x text-dark",
            N: "fas fa-chess-knight fa-3x text-dark",
            B: "fas fa-chess-bishop fa-3x text-dark",
            R: "fas fa-chess-rook fa-3x text-dark",
            Q: "fas fa-chess-queen fa-3x text-dark",
            K: "fas fa-chess-king fa-3x text-dark",
        };

        switch (color) {
            case "w":
                return w[token];
            case "b":
                return b[token];
            default: break;
        }
    }

    selected(b) {
        console.log(b)
    }

    render() {
        return (
            <section className="container text-center border border-5 my-5 mx-auto">
                {this.state.board.map((r, i) => {
                    console.log(i)
                    return (
                        <div className="row" key="i">
                            {r.map((c, k) => {
                                const color = (i === 1 || i === 2) ? "b" : "w";

                                if (i === 0 || i === r.length - 1) {
                                    return (<div className="col letter" key="k"> {c} </div>)
                                } else if (k === 0 || k === r.length - 1) {
                                    return (<div className="col number" key="k"> {c} </div>)
                                } else if (k % 2 !== i % 2) {
                                    return (
                                        <div className="col" key="k">
                                            <button className="square black" onClick={(b) => this.selected(b)}>
                                                <i className={this.getToken(r[k], color)}></i>
                                            </button>
                                        </div>
                                    )
                                } else {
                                    return (
                                        <div className="col" key="k">
                                            <button className="square white">
                                                <i className={this.getToken(r[k], color)}></i>
                                            </button>
                                        </div>
                                    )
                                }
                            })}
                        </div>
                    )
                })}
            </section>
        )
    }
}

export default Board;
