class Movements {
    PAWN_OFFSETS = {
        b: [16, 32, 17, 15],
        w: [-16, -32, -17, -15]
    }

    PIECE_OFFSETS = {
        n: [-18, -33, -31, -14, 18, 33, 31, 14],
        b: [-17, -15, 17, 15],
        r: [-16, 1, 16, -1],
        q: [-17, -16, -15, 1, 17, 16, 15, -1],
        k: [-17, -16, -15, 1, 17, 16, 15, -1]
    }

    checkMovement(piece, from, to) {
        switch (piece) {
            case "P":
                for (let i = 0; i < this.PAWN_OFFSETS.b.length; i++) {
                    if (from - to === this.PAWN_OFFSETS.b[i]) {
                        console.log(from - to)
                        console.log(this.PAWN_OFFSETS.b[i])
                    }
                }
                break
            case "R":
            case "N":
            case "B":
            case "Q":
            case "K":
            default: return false;
        }
    }

    /*****************************************************************************
     * UTILITY FUNCTIONS
     ****************************************************************************/
    rank(i) {
        return i >> 4;
    }
}

export default Movements
