/* Framework */
import React from 'react';

/* Style CSS, SASS, LESS */
import './App.css';

/* Components */
import Board from './components/Board';
import Menu from './components/Menu';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      startPlayer: ["white", "black"][Math.floor(Math.random()*2)],
      nextPlayer: null
    }
  }

  handleClick(el) {
    console.log(el.target)
  }

  render() {
    return (
      <React.Fragment>
        <Menu />
        <Board onClick={(el) => this.handleClick(el)}/>
      </React.Fragment>
    )
  }
}

export default App;
