import React from 'react';
import logo from './logo.svg';
import './App.css';

const letters = ["", "A", "B", "C", "D", "E", "F", "G", "H", ""];
const numbers = ["1", "2", "3", "4", "5", "6", "7", "8"];

function Board(Rows) {
  return class extends React.Component {
    render() {
      return (
        <section className="container border border-5">
          <Rows />
        </section>
      )
    }
  }
}

function App() {
  let rows = [];

  for (let i = 1; i <= 64; i++) {
    if (i % 8 === 0) {
      rows.push(
        <div className="row">
          <div className="col square"> {i} </div>
        </div>
      )
    }
  }


  return (
    /* <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div> */

    <section className="container border border-5">
      <div className="row">
        {letters.map((l, k) => {
          return (
            <div className="col square"> {l}</div>
          )
        })}
      </div>

      <div className="row">
        {numbers.map((n, k) => {
          if (k == 0) {
            return (
              <div className="col square"> {n}</div>
            )
          } else {
            if (k % 2 == 0) {
              return (
                <div className="col">
                  <div className="square black"></div>
                </div>
              )
            } else if (k == numbers.length - 1) {
              return (
                <div className="col">
                  <div className="square white"></div>
                </div>
              )
            }
          }

        })}
      </div>
    </section>
  );
}

export default App;
