/* Framework */
import React from 'react';

/* Style CSS, SASS, LESS */
import './App.css';

/* Components */
import Board from './components/Board';
import ScoreBoard from './components/ScoreBoard';
import Menu from './components/Menu';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isMoving: false,
      startPlayer: ["white", "black"][Math.floor(Math.random() * 2)],
      nextPlayer: null,
      emptySquare: "&nbsp;",
      currentSelected: {
        item: null,
        position: null
      }
    }
  }

  componentDidMount() {
    this.setState({
      nextPlayer: this.state.startPlayer,
    })
  }

  updateCurrentSelected(item, position) {
    if(item === null && position === null) {
      let el = this.state.currentSelected.item
      el.innerHTML = ""
    }

    this.setState({
      currentSelected: {
        item: item,
        position: position
      }
    })
  }

  updateCurrentPlayer() {
    this.setState({
      nextPlayer: this.state.nextPlayer === "black" ? "white" : "black"
    })
  }

  updateMovementState() {
    this.setState({
      isMoving: !this.state.isMoving
    })
  }

  handleClick(el) {
    if (!this.state.isMoving && el.currentTarget.firstElementChild) {
      el.currentTarget.style.opacity = "0.4"
      this.updateCurrentSelected(el.currentTarget, el.currentTarget.dataset.position)
      this.updateMovementState()
    } else if (this.state.isMoving) {
      el.currentTarget.innerHTML = this.state.currentSelected.item.innerHTML
      this.updateCurrentSelected(null, null)
      this.updateMovementState()
    }
  }

  render() {
    return (
      <React.Fragment>
        <Menu />
        <div className="container-fluid">
          <div className="row">
            <div className="col-12 col-lg-10">
              <Board nextPlayer={this.state.nextPlayer} onClick={(el) => this.handleClick(el)} />
            </div>

            <div className="col-12 col-lg-2 order-1 order-lg-2">
              <ScoreBoard nextPlayer={this.state.nextPlayer} />
            </div>
          </div>
        </div>
      </React.Fragment>
    )
  }
}

export default App;
