import React from "react";

class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            board: [
                ["", "A", "B", "C", "D", "E", "F", "G", "H", ""],
                ["8", "R", "K", "B", "Q", "K", "B", "K", "R", "8"],
                ["7", "P", "P", "P", "P", "P", "P", "P", "P", "7"],
                ["6", "", "", "", "", "", "", "", "", "6"],
                ["5", "", "", "", "", "", "", "", "", "5"],
                ["4", "", "", "", "", "", "", "", "", "4"],
                ["3", "", "", "", "", "", "", "", "", "3"],
                ["2", "P", "P", "P", "P", "P", "P", "P", "P", "2"],
                ["1", "R", "K", "B", "Q", "K", "B", "K", "R", "1"],
                ["", "A", "B", "C", "D", "E", "F", "G", "H", ""],
            ],
        }
    }

    getPiece(token, color) {
        const w = [
            P => "fas fa-chess-",
            K => "fas fa-chess-",
            B => "fas fa-chess-",
            R => "fas fa-chess-",
            Q => "fas fa-chess-",
            K => "fas fa-chess-",
        ];

        const b = [
            P => "fas fa-chess-",
            K => "fas fa-chess-",
            B => "fas fa-chess-",
            R => "fas fa-chess-",
            Q => "fas fa-chess-",
            K => "fas fa-chess-",
        ];

        switch (color) {
            case "w":
                return w[token];
            case "b":
                return b[token];
            default: break;
        }

    }

    render() {
        return (
            <section className="container text-center border border-5 my-5 mx-auto">
                {this.state.board.map((r, i) => {
                    return (
                        <div className="row">
                            {r.map((c, k) => {
                                console.log(r.length)
                                if (i === 0 || i === r.length - 1) {
                                    return (<div className="col letter"> {c} </div>)
                                } else if (k === 0 || k === r.length - 1) {
                                    return (<div className="col number"> {c} </div>)
                                } else if (k % 2 !== i % 2) {
                                    const fontAwesome = `fas fa-chess-${r[k]}`
                                    return (
                                        <div className="col square black">
                                            <i className="fas fa-chess-pawn"></i>
                                            {r[k]}
                                        </div>
                                    )
                                } else {
                                    return (
                                        <div className="col square white">
                                            {r[k]}
                                        </div>
                                    )
                                }
                            })}
                        </div>
                    )
                })}
            </section>
        )
    }
}

export default Board;
