import React from "react";

class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            board: [
                ["", "A", "B", "C", "D", "E", "F", "G", "H", ""],
                ["8", "R", "K", "B", "Q", "K", "B", "K", "R", "8"],
                ["7", "P", "P", "P", "P", "P", "P", "P", "P", "7"],
                ["6", "P", "P", "P", "P", "P", "P", "P", "P", "6"],
                ["5", "P", "P", "P", "P", "P", "P", "P", "P", "5"],
                ["4", "P", "P", "P", "P", "P", "P", "P", "P", "4"],
                ["3", "P", "P", "P", "P", "P", "P", "P", "P", "3"],
                ["2", "P", "P", "P", "P", "P", "P", "P", "P", "2"],
                ["1", "R", "K", "B", "Q", "K", "B", "K", "R", "1"],
                ["", "A", "B", "C", "D", "E", "F", "G", "H", ""],
            ],
        }
    }

    render() {
        return (
            <section className="container text-center border border-5 my-1 mx-auto">
                {this.state.board.map((r, i) => {
                    return (
                        <div className="row">
                            {r.map((c, k) => {
                                console.log(r.length)
                                if (i === 0 || i === r.length - 1 || k === 0 || k === r.length - 1) {
                                    return (<div className="col square align-middle"> {c} </div>)
                                } else if (k % 2 !== i % 2) {
                                    return (
                                        <div className="col square black align-middle"> {r[k]} </div>
                                    )
                                } else {
                                    return (
                                        <div className="col square white align-middle"> {r[k]} </div>
                                    )
                                }
                            })}
                        </div>
                    )
                })}
            </section>
        )
    }
}

export default Board;
