import React from "react";

class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            startBoard: [
                ["", "A", "B", "C", "D", "E", "F", "G", "H", ""],
                ["8", "R", "N", "B", "Q", "K", "B", "N", "R", "8"],
                ["7", "P", "P", "P", "P", "P", "P", "P", "P", "7"],
                ["6", null, null, null, null, null, null, null, null, "6"],
                ["5", null, null, null, null, null, null, null, null, "5"],
                ["4", null, null, null, null, null, null, null, null, "4"],
                ["3", null, null, null, null, null, null, null, null, "3"],
                ["2", "P", "P", "P", "P", "P", "P", "P", "P", "2"],
                ["1", "R", "N", "B", "Q", "K", "B", "N", "R", "1"],
                ["", "A", "B", "C", "D", "E", "F", "G", "H", ""],
            ],
            pieces: this.props.pieces["PIECES"]
        }
    }

    render() {
        const pieces = this.state.pieces;

        return (
            <section className="container text-center border border-5 my-5 mx-auto">
                {this.state.startBoard.map((row, i) => {
                    return (
                        <div className="row" key={i}>
                            {row.map((square, k) => {
                                const color = (i === 1 || i === 2) ? "BLACK" : "WHITE";

                                if (i === 0 || i === row.length - 1) {
                                    return (<div className="col letter" key={k}> {square} </div>)
                                } else if (k === 0 || k === row.length - 1) {
                                    return (<div className="col number" key={k}> {square} </div>)
                                } else if (k % 2 !== i % 2) {
                                    if (square === null) {
                                        return (
                                            <div className="col" key={k}>
                                                <button className="square black" data-piece={square} data-position={`${i}-${k}`} data-player={this.props.nextPlayer} onClick={(b) => this.props.onClick(b)}>
                                                    &nbsp;
                                                </button>
                                            </div>
                                        )
                                    } else {
                                        return (
                                            <div className="col" key={k}>
                                                <button className="square black" data-piece={square} data-position={`${i}-${k}`} data-player={color} onClick={(b) => this.props.onClick(b)}>
                                                    <i className={pieces[color][square]}></i>
                                                </button>
                                            </div>
                                        )
                                    }
                                } else {
                                    if (row[k] === null) {
                                        return (
                                            <div className="col" key={k}>
                                                <button className="square white" data-piece={square} data-position={`${i}-${k}`} data-player={this.props.nextPlayer} onClick={(b) => this.props.onClick(b)}>
                                                    &nbsp;
                                                </button>
                                            </div>
                                        )
                                    } else {
                                        return (
                                            <div className="col" key={k}>
                                                <button className="square white" data-piece={square} data-position={`${i}-${k}`} data-player={color} onClick={(b) => this.props.onClick(b)}>
                                                    <i className={pieces[color][square]}></i>
                                                </button>
                                            </div>
                                        )
                                    }
                                }
                            })}
                        </div>
                    )
                })}
            </section>
        )
    }
}

export default Board;
