/* Framework */
import React from 'react';

/* Style CSS, SASS, LESS */
import './App.css';

/* Components */
import Board from './components/Board';
import ScoreBoard from './components/ScoreBoard';
import Menu from './components/Menu';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      startPlayer: ["white", "black"][Math.floor(Math.random() * 2)],
      emptySquare: "&nbsp;"
    }
  }

  componentDidMount() {
    this.setState({
      nextPlayer: this.state.startPlayer,
    })
  }

  handleClick(el) {
    el.target.removeChild(el.target.children[0])
    console.log(el.currentTarget.children)
    console.log(el.currentTarget.dataset.position)
  }

  render() {
    return (
      <React.Fragment>
        <Menu />
        <div className="container-fluid">
          <div className="row">
            <div className="col-12 col-lg-10">
              <Board onClick={(el) => this.handleClick(el)} />
            </div>

            <div className="col-12 col-lg-2 order-1 order-lg-2">
              <ScoreBoard nextPlayer={this.state.nextPlayer} />
            </div>
          </div>
        </div>


      </React.Fragment>
    )
  }
}

export default App;
