import React from "react";

class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            board: [
                ["", "A", "B", "C", "D", "E", "F", "G", "H", ""],
                ["8", "R", "N", "B", "Q", "K", "B", "N", "R", "8"],
                ["7", "P", "P", "P", "P", "P", "P", "P", "P", "7"],
                ["6", null, null, null, null, null, null, null, null, "6"],
                ["5", null, null, null, null, null, null, null, null, "5"],
                ["4", null, null, null, null, null, null, null, null, "4"],
                ["3", null, null, null, null, null, null, null, null, "3"],
                ["2", "P", "P", "P", "P", "P", "P", "P", "P", "2"],
                ["1", "R", "N", "B", "Q", "K", "B", "N", "R", "1"],
                ["", "A", "B", "C", "D", "E", "F", "G", "H", ""],
            ],
            pieces: {
                white: {
                    P: "fas fa-chess-pawn fa-3x text-white",
                    N: "fas fa-chess-knight fa-3x text-white",
                    B: "fas fa-chess-bishop fa-3x text-white",
                    R: "fas fa-chess-rook fa-3x text-white",
                    Q: "fas fa-chess-queen fa-3x text-white",
                    K: "fas fa-chess-king fa-3x text-white",
                },
                black: {
                    P: "fas fa-chess-pawn fa-3x text-dark",
                    N: "fas fa-chess-knight fa-3x text-dark",
                    B: "fas fa-chess-bishop fa-3x text-dark",
                    R: "fas fa-chess-rook fa-3x text-dark",
                    Q: "fas fa-chess-queen fa-3x text-dark",
                    K: "fas fa-chess-king fa-3x text-dark",
                }
            }

        }
    }

    getToken(token, color) {
        switch (color) {
            case "w":
                return this.state.pieces.white[token];
            case "b":
                return this.state.pieces.black[token];
            default: break;
        }
    }

    selected(b) {
        console.log(b.target)
    }

    render() {
        return (
            <section className="container text-center border border-5 my-5 mx-auto">
                {this.state.board.map((row, i) => {
                    console.log(i)
                    return (
                        <div className="row" key={i}>
                            {r.map((square, k) => {
                                const color = (i === 1 || i === 2) ? "black" : "white";

                                console.log(c)
                                if (i === 0 || i === r.length - 1) {
                                    return (<div className="col letter" key={k}> {c} </div>)
                                } else if (k === 0 || k === r.length - 1) {
                                    return (<div className="col number" key={k}> {c} </div>)
                                } else if (k % 2 !== i % 2) {
                                    if (c === null) {
                                        return (
                                            <div className="col" key={k}>
                                                <span className="square black">&nbsp;</span>
                                            </div>
                                        )
                                    } else {
                                        return (
                                            <div className="col" key={k}>
                                                <a href="!#" className="square black" onClick={(b) => this.selected(b)}>
                                                    <i className={this.state.pieces[color][c]}></i>
                                                </a>
                                            </div>
                                        )
                                    }
                                } else {
                                    if (r[k] === null) {
                                        return (
                                            <div className="col" key={k}>
                                                <span className="square white">&nbsp;</span>
                                            </div>
                                        )
                                    } else {
                                        return (
                                            <div className="col" key={k}>
                                                <a href="!#" className="square white" onClick={(b) => this.selected(b)}>
                                                    <i className={this.state.pieces[color][c]}></i>
                                                </a>
                                            </div>
                                        )
                                    }
                                }
                            })}
                        </div>
                    )
                })}
            </section>
        )
    }
}

export default Board;
