import React from "react";

class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            board: [
                ["", "A", "B", "C", "D", "E", "F", "G", "H", ""],
                ["8", "R", "N", "B", "Q", "K", "B", "N", "R", "8"],
                ["7", "P", "P", "P", "P", "P", "P", "P", "P", "7"],
                ["6", "", "", "", "", "", "", "", "", "6"],
                ["5", "", "", "", "", "", "", "", "", "5"],
                ["4", "", "", "", "", "", "", "", "", "4"],
                ["3", "", "", "", "", "", "", "", "", "3"],
                ["2", "P", "P", "P", "P", "P", "P", "P", "P", "2"],
                ["1", "R", "N", "B", "Q", "K", "B", "N", "R", "1"],
                ["", "A", "B", "C", "D", "E", "F", "G", "H", ""],
            ],
        }
    }

    getToken(token, color) {
        const w = {
            P: "far fa-chess-pawn",
            N: "far fa-chess-knight",
            B: "far fa-chess-bishop",
            R: "far fa-chess-rock",
            Q: "far fa-chess-queen",
            K: "far fa-chess-king",
        };

        const b = {
            P: "fas fa-chess-pawn",
            N: "fas fa-chess-knight",
            B: "fas fa-chess-bishop",
            R: "fas fa-chess-rock",
            Q: "fas fa-chess-queen",
            K: "fas fa-chess-king",
        };
        switch (color) {
            case "w":
                return w[token];
            case "b":
                return b[token];
            default: break;
        }
    }

    render() {
        return (
            <section className="container text-center border border-5 my-5 mx-auto">
                {this.state.board.map((r, i) => {
                    return (
                        <div className="row">
                            {r.map((c, k) => {
                                if (i === 0 || i === r.length - 1) {
                                    return (<div className="col letter"> {c} </div>)
                                } else if (k === 0 || k === r.length - 1) {
                                    return (<div className="col number"> {c} </div>)
                                } else if (k % 2 !== i % 2) {
                                    return (
                                        <div className="col square black">
                                            <i className={this.getToken(r[k], "b")}></i>
                                        </div>
                                    )
                                } else {
                                    return (
                                        <div className="col square white">
                                            <i className={this.getToken(r[k], "w")}></i>
                                        </div>
                                    )
                                }
                            })}
                        </div>
                    )
                })}
            </section>
        )
    }
}

export default Board;
