import React from "react";

class ScoreBoard extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            nextPlayer: this.props.nextPlayer,
        }
    }
    render() {
        return (
            <React.Fragment>
            {console.log('scoreBoard', this.props.nextPlayer)}
                <section className="container-fluid text-center my-5">
                    <div className="row">
                        <div className="col">
                            <h1>MARCADOR</h1>
                            <h2>Jugador: {this.props.nextPlayer}</h2>
                        </div>
                    </div>
                </section>
            </React.Fragment>
        )
    }
}

export default ScoreBoard;
